import { createGlobalStyle } from 'styled-components';
// propriedades compartilhadas por toda a aplicação
export default createGlobalStyle`
    * {
        margin: 0;
        padding: 0;
        outline: 0;
        box-sizing: border-box;
    }

    html, body, #root{
        min-height: 100%;
    }

    body{
        background: #7159c1;
        -webkit-font-smoothing : antialiased !important;
    }


`;
